Script.Lua:

-- 19116
local packet = CreateFakePacket(0x0505);
print("Start CMSG_CREATURE_QUERY");
for i = 1, 100000 do
    packet:Clear();
    packet:WriteInt32(i);
    packet:Send();
    if ((i % 100) == 0) then
        print(string.format("Send Entry: %d, Dump: %s", i, packet:Dump()));
    end
    sleep(50);
end

print("Done!");
